// Gets the current contact
ContactInfo currentContact = ContactManagementContext.GetCurrentContact();

// Gets values from the contact's properties
string contactName = currentContact.ContactLastName;
string contactJobTitle = currentContact.ContactJobTitle;

// Gets a collection of objects representing article pages using a generated provider
IEnumerable<Article> articles = ArticleProvider.GetArticles()
    .OnSite("MySite")
    .Culture("en-US")
    .Path("/Articles/", PathTypeEnum.Children);

// Gets an instance of the shopping service used to manage the current shopping cart
IShoppingService shoppingService = Service.Resolve<IShoppingService>();

// Removes all products from the current shopping cart
shoppingService.RemoveAllItemsFromCart();
