public CompanyDto GetCompany()
        {
            return DocumentQueryService.GetDocuments<CMS.DocumentEngine.Types.Training.Company>()
                .AddColumns("CompanyName", "Street", "City", "Country", "ZipCode", "PhoneNumber", "Email")
                .TopN(1)
                .ToList()
                // Splits the Country field which stores country and state values
                .Select(m =>
                {
                    var splitCountry = m.Country.Split(';');

                    string country;
                    string state = null;
                    if (splitCountry.Length == 2)
                    {
                        country = splitCountry[0];
                        state = splitCountry[1];
                    }
                    else
                    {
                        country = m.Country;
                    }

                    return new CompanyDto()
                    {
                        Name = m.CompanyName,
                        City = m.City,
                        Street = m.Street,
                        Country = country,
                        State = state,
                        Email = m.Email,
                        PhoneNumber = m.PhoneNumber,
                        ZipCode = m.ZipCode
                    };
                })
                .FirstOrDefault();
        }

