slideshowWidget.collectImageIds = function (swiper) {
	var output = [];

	for (var s = 0; s <= swiper.slides.length - 1; s++) {
		var childElement = swiper.slides[s].children[0];
		output.push(childElement.id);
	}

	return output;
};

